    private void btnLogarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnLogarActionPerformed
        String emailRecebido = jTextEmail.getText();
        String senhaRecebida = String.valueOf(jPassSenha.getPassword());
        
        String emailCorreto = "marcelo.amaro@aluno.uece.br";
        String senhaCorreta = "987";
        
        if(emailRecebido.equals(emailCorreto) && senhaRecebida.equals(senhaCorreta)){
            this.dispose();
            Principal telaPrincipal = new Principal();
            telaPrincipal.show();
            
        }else{
            JOptionPane.showMessageDialog(null, "Ops!, Email ou senha incorreta!");
            jTextEmail.setText("");
            jPassSenha.setText("");
        }
        
    }//GEN-LAST:event_btnLogarActionPerformed

